import logo from './logo.svg';
import './App.css';
import React from 'react'
import "./styles.css";
import AngleDial from './AngleDial.js'

class App extends React.Component{

    // Constructor 
    constructor(props) {
        super(props);

        this.state = {
            agents: [],
            DataisLoaded: false
        };
    }

    // Fetch the agent information
    componentDidMount() {
        fetch("/agents")
            .then((res) => res.json())
            .then((json) => {
                this.setState({
                    agents: json,
                    DataisLoaded: true
                });
            })
    }

    // Render agent information to the screen
    render() {
        const { DataisLoaded, agents } = this.state;
        if (!DataisLoaded) return <div>
            <h1> Please wait some time.... </h1> </div> ;

        return (
        <div className = "App">
            <h1> Fetch data from an api in reacts </h1> {
                agents.map((agent) => ( 
                <ol key = { agent.id } >
                    agent_id: { agent.agent_id }, 
                    cur_pos: { agent.cur_pos }, 
                    angle: { agent.cur_angle } ,
                    ag_color: { agent.color }
                    <AngleDial value={agent.angle}/>
                    </ol>
                ))
            }
        </div>
    );
}
}


export default App;
