// Import React
import React from "react";

// Import Basic Knob from react-dial-kknob
import { Basic } from "react-dial-knob";

// Import io from socket to send to backend flask
import io from 'socket.io-client';

// Create the socket
const socket = io();

// Component for AngleDial (rendered from Agent)
class AngleDial extends React.Component {

  // Dial has a cur_angle and an id
  constructor(props) {
    super(props);
    this.state = {
      value: props.cur_angle,
      id: props.agent_id,
    };
  }

  componentDidMount() {
    setInterval(() => {
      socket.emit('agent_angle',
                    {
                        'id':this.state.id,
                        'value':this.state.value
                    }); // End emit
        }, 2000); // End Interval
  } // End did mount

    handleChange = (newValue) => {
      this.setState({value: newValue});
    };


  render() {

  return (<Basic
        diameter={200}
        min={0}
        max={360}
        step={1}
        value={this.state.value}
        theme={{
            defaultColor: '#333',
            activeColor: '#3f3'
        }}
        style={{
          position: 'relative',
          margin: '100px auto',
          width: '200px',
        }}
        onValueChange={this.handleChange}
        // Use onInteractionChange to emit to socket the final placement
        ariaLabelledBy={'my-label'}
        ariaValueText={'value-label'}
        spaceMaxFromZero={false}
    >
        <label id={'my-label'} style={{
          textAlign: 'center',
          width: '200px',
          display: 'block',
          padding: '10px 0'
        }}>My Label</label>
        <label id={'value-label'} style={{
          textAlign: 'center',
          width: '200px',
          display: 'block',
          padding: '10px 0',
        }}> {this.state.value} </label>
    </Basic> );
    }
}


export default AngleDial;
