import React from 'react';
import {
  Chart as ChartJS,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from 'chart.js';
import { Scatter } from 'react-chartjs-2';

ChartJS.register(LinearScale, PointElement, LineElement, Tooltip, Legend);

class AgentMap extends React.Component {

  // Constructor
  constructor(props) {
    super(props);

    // Init Datasets
    const datasets = [];
    this.props.agents.forEach(function (agent) {
      datasets.push({
        label: agent.agent_id,
        data: [agent.cur_pos],
        backgroundColor: agent.color,
      });
    });

    // Init State
    this.state = {
      agents: this.props.agents,
      options: {
        scales: {
          y: {
            beginAtZero: true,
            min : 0,
            max : this.props.max_NS,
            reverse : true,
          },
          x: {
            beginAtZero: true,
            min : 0,
            max : this.props.max_EW,
          },
          grid: {
            borderColor : 'Dark Blue',
            borderWidth : this.props.tile_size,

          }
        },
      },
      data:{datasets},
    };
    console.log(this.state.data);
  }

  render() {
    return <Scatter options={this.state.options} data={this.state.data} />;
  }
}

export default AgentMap;
