import React from 'react';
import {
  Chart as ChartJS,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from 'chart.js';
import { Scatter } from 'react-chartjs-2';

ChartJS.register(LinearScale, PointElement, LineElement, Tooltip, Legend);

class AgentMap extends React.Component {

  // Constructor
  constructor(props) {
    super(props);

    // Init Datasets
    const datasets = [];
    this.props.agents.forEach(function (agent) {
      datasets.push({
        label: agent.agent_id,
        data: [agent.cur_pos],
        backgroundColor: agent.color,
      });
    });

    // Init State
    this.state = {
      agents: this.props.agents,
      options: {

        // Parsing
        parsing: {
            xAxisKey: 'x',
            yAxisKey: 'z',
        },

        // Scales x and y
        scales: {
          y: {
            beginAtZero: true,
            min : 0,
            max : this.props.max_NS,
            reverse : true,
            grid : {
                color: 'LightGray',
            },
            ticks: {
              stepSize: this.props.tile_size,
            }

          }, // End y
          x: {
            beginAtZero: true,
            min : 0,
            max : this.props.max_EW,
            grid : {
                color: 'LightGray',
            },
            ticks: {
              stepSize: this.props.tile_size,
            }
          }, // End x
        }, // End Scales

        // Plugins
        plugins: {
            dragData: {
              round: 3,
              showTooltip: true,
              onDragEnd: update_agent_pos,
            },
        },

      }, // End options
      data:{datasets},
    }; // End state
    console.log(this.state.data);
    this.update_agent_pos = this.update_agent_pos.bind(this);
  }

  update_agent_pos(e, datasetIndex, index, value) {
    console.log('E');
    console.log(e);
    console.log('datasetIndex');
    console.log(datasetIndex);
    console.log('index');
    console.log(index);
    console.log('value');
    console.log(value);
  }

  render() {
    return <Scatter options={this.state.options} data={this.state.data} />;
  }
}

export default AgentMap;
