import React from "react";
import { Basic } from "react-dial-knob";
import io from 'socket.io-client';

// You can try Basic, White, Silver or HighContrast skins

const socket = io();

class AngleDial extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: props.value,
    };
  }

  componentDidMount() {
    const newIntervalId = setInterval(() => {
      socket.emit(this.state.value);
    }, 1000);
  }

    handleChange = (newValue) => {
      this.setState({value: newValue});
    };


  render() {

  return (<Basic
        diameter={200}
        min={0}
        max={360}
        step={1}
        value={this.state.value}
        theme={{
            defaultColor: '#333',
            activeColor: '#3f3'
        }}
        style={{
          position: 'relative',
          margin: '100px auto',
          width: '200px',
        }}
        onValueChange={this.handleChange}
        // Use onInteractionChange to emit to socket the final placement
        ariaLabelledBy={'my-label'}
        ariaValueText={'value-label'}
        spaceMaxFromZero={false}
    >
        <label id={'my-label'} style={{
          textAlign: 'center',
          width: '200px',
          display: 'block',
          padding: '10px 0'
        }}>My Label</label>
        <label id={'value-label'} style={{
          textAlign: 'center',
          width: '200px',
          display: 'block',
          padding: '10px 0',
        }}> {this.state.value} </label>
    </Basic> );
    }
}


export default AngleDial;
