import React from 'react';
import {
  Chart as ChartJS,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from 'chart.js';
import { Scatter } from 'react-chartjs-2';
import 'chartjs-plugin-dragdata';

ChartJS.register(LinearScale, PointElement, LineElement, Tooltip, Legend);

class AgentMap extends React.Component {

  // Constructor
  constructor(props) {
    super(props);

    // Init Datasets
    const datasets = [];
    this.props.agents.forEach(function (agent) {
      datasets.push({
        label: agent.agent_id,
        data: [agent.cur_pos],
        backgroundColor: agent.color,
      });
    });

    this.update_agent_pos = this.update_agent_pos.bind(this);
    //
    // Init State
    this.state = {
      agents: this.props.agents,

      data:{datasets},
    }; // End state
    console.log(this.state.data);
  }

  update_agent_pos(e, datasetIndex, index, value) {
    console.log('E');
    console.log(e);
    console.log('datasetIndex');
    console.log(datasetIndex);
    console.log('index');
    console.log(index);
    console.log('value');
    console.log(value);
  }

  render() {
    const options = {
          // Parsing
          parsing: {
              xAxisKey: 'x',
              yAxisKey: 'z',
          },

          // Scales x and y
          scales: {
            y: {
              beginAtZero: true,
              min : 0,
              max : this.props.max_NS,
              reverse : true,
              grid : {
                  color: 'LightGray',
              },
              ticks: {
                stepSize: this.props.tile_size,
              }

            }, // End y
            x: {
              beginAtZero: true,
              min : 0,
              max : this.props.max_EW,
              grid : {
                  color: 'LightGray',
              },
              ticks: {
                stepSize: this.props.tile_size,
              }
            }, // End x
          }, // End Scales

          // Plugins
          plugins: {
              dragData: {
                round: 1,
                showTooltip: true,
                onDragStart:function (e, datasetIndex, index, value) {
                },
                onDrag: function (e, datasetIndex, index, value) {
                    e.target.style.cursor = 'grabbing'
                    //console.log("Drag Value: ", value.x)
                },
                onDragEnd: function (e, datasetIndex, index, value) {
                    e.target.style.cursor = 'default'
                },
              }, // End drag Data
          }, // End plugin
          responsive: true,
        }; // End options  

     return <Scatter options={options} data={this.state.data} />;
  }
}

export default AgentMap;
