// Import styplesheet for App
import './App.css';

// Import React for components
import React from 'react'

// Import styles (contains stuff for knob
import Agent from './Agent.js'

class App extends React.Component{

    // Constructor 
    constructor(props) {
        super(props);

        this.state = {
            agents: [],
            DataisLoaded: false
        };
    }

    // Fetch the agent information
    componentDidMount() {
        fetch("/agents")
            .then((res) => res.json())
            .then((json) => {
                this.setState({
                    agents: json,
                    DataisLoaded: true
                });
            })
    }

    // Render agent information to the screen
    render() {
        const { DataisLoaded, agents } = this.state;
        if (!DataisLoaded) return <div>
            <h1> Please wait some time.... </h1> </div> ;

        return (
        <div className = "App">
            <h1> Fetch data from an api in reacts </h1> {
                agents.map((agent) => ( 

                    <Agent agent_id={agent.agent_id} 
                           cur_angle={agent.cur_angle}
                           cur_pos={agent.cur_pos}
                           color={agent.color} />

                ))
            }
        </div>
    );
}
}


export default App;
